import { createStore } from 'vuex'

export default createStore({
    state: {
        progress: 0
    },
    getters: {
    },
    mutations: {
        add(state, value) {
            state.progress = value
        }
    },
    actions: {
        updateAction(context, value) {
            context.commit('add', value)
        }
    },
    modules: {
    }
})
