import { createStore } from 'vuex'

export default createStore({
    state: {
        fileName: '',
        uploadProgress: 0,
        upServiceCount: 0,
        downServiceCount: 0,
        executionTime: 0

    },
    getters: {
    },
    mutations: {
        updateProgress(state, value) {
            state.uploadProgress = value
        },
        updateFileName(state, value) {
            state.fileName = value
        },
        updateUpServiceCount(state, value) {
            state.upServiceCount = value
        },
        updateDownServiceCount(state, value) {
            state.downServiceCount = value
        }
    },
    actions: {
        updateProgressAction(context, value) {
            context.commit('updateProgress', value)
        },
        updateFileNameAction(context, value) {
            context.commit('updateFileName', value)
        },
        updateUpServiceCountAction(context, value) {
            context.commit('updateUpServiceCount', value)
        },
        updateDownServiceCountAction(context, value) {
            context.commit('updateDownServiceCount', value)
        }
    },
    modules: {
    }
})
