import { createStore } from 'vuex'

export default createStore({
    state: {
        fileName: '',
        uploadProgress: 0,
    },
    getters: {
    },
    mutations: {
        updateProgress(state, value) {
            state.uploadProgress = value
        },
        updateFileName(state, value) {
            state.fileName = value
        },
        updateUpServiceAmount(state, value) {
            state.upServiceAmount = value
        },
        updateDownServiceAmount(state, value) {
            state.downServiceAmount = value
        },
        updateExecutionTime(state, value) {
            state.executionTime = value
        }
    },
    actions: {
        updateProgressAction(context, value) {
            context.commit('updateProgress', value)
        },
        updateFileNameAction(context, value) {
            context.commit('updateFileName', value)
        },
        updateUpServiceAmountAction(context, value) {
            context.commit('updateUpServiceAmount', value)
        },
        updateDownServiceAmountAction(context, value) {
            context.commit('updateDownServiceAmount', value)
        },
        updateExecutionTimeAction(context, value) {
            context.commit('updateExecutionTime', value)
        }
    },
    modules: {
    }
})
